import React from "react"
import axios from "axios";
import { Link } from 'react-router-dom';
import Navbar from './Navbar'


class Userstory extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allStory: [],
            user_id: '',
            Comments_all: [],
            tokenId: window.localStorage.getItem("token")

        }

    }

    componentDidMount = (e) => {
        axios.get('http://127.0.0.1:5000/read_userStory/' + this.props.match.params.user_id)
            .then(res => {
                this.setState({
                    allStory: res.data,
                })
                console.log(res.data)
            })
            .catch((err) => alert(err))
    }
    Deleteblog = (blog_id) => {
        axios.delete('http://127.0.0.1:5000/delete-blog/' + blog_id, {
            headers: {
                user_id: this.state.user_id
            }
        })
            .then((res) => {
                console.log(res.data)
                this.props.history.push(`/Blog/${this.state.user_id}`)
                window.location.reload(false)
            })
            .catch((error) => alert(error))
    }

    render() {
        let all_blog = this.state.allStory.reverse().map((e) => {
                return (
                    <div className="card w-50 mb-4 offset-3 text-center" >

                        <img class="w-100" style={{ height: "300px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`} alt=""></img>
                        <h4> content : {e.first_name}</h4>
                        <div>
                            <Link to={`/Comment/${e.story_id}`}><button type="submit" className="btn btn-info"> Show More </button></Link>
                            <button type="submit" className="btn btn-danger ml-5" onClick={() => this.Deleteblog(e.story_id)}> Delete Blog </button>
                            <Link to={`/Edit/${e.story_id}`}><button type="submit" className="btn btn-primary ml-5"> Edit Blog </button></Link>

                        </div>
                    </div>
                )


        })
        return (
            <div>
                <Navbar />
                <h2 className="offset-5">Blog Page</h2>
                {all_blog}


            </div>
        )
    }

}

export default Userstory
