import React from 'react';
import Axios from 'axios';


 class  SignUpUser extends React.Component {
     constructor(props){
         super(props)
         this.state={
            first_name :"",
            last_name:"",
             email:"",
             mobile:"",
             password:"",
             Gender:"",
             DateOfBirth:""

         }
     }
     radioChange = (e)=>{
         this.setState({Gender:e.target.value})
     }
     InputChange = (input) =>{
         this.setState({
             [input.target.name]:input.target.name
         })
     }

     SignupSubmit = (user) =>{
         let UserData = {
            first_name: this.state.first_name,
            last_name:this.state.last_name,
            email: this.state.email,
            password: this.state.password,
            mobile: this.state.mobile,
            Gender:this.state.Gender,
            DateOfBirth:this.state.DateOfBirth
         }
         Axios.post('http://127.0.0.1:5000/signup_user',UserData)
         .then((res) =>{
             console.log(res)
         })
     }
     render(){
         console.log(this.state.Gender)
         return(
             <div className = "mt-5">
                 <h3 className = "h3"> Create an account</h3>
        <form onSubmit = {this.SignupSubmit}>
            <div class="form-row">
                <div class="col-4">
                <input type="text" class="form-control w-100" placeholder="First name" onChange = {this.InputChange} />
                </div>
                <div class="col-4">
                <input type="text" class="form-control w-100" placeholder="Last name" onChange = {this.InputChange}/>
                </div>
                <div class="col-8 mt-4">
                <input type="text" class="form-control w-100" placeholder="Mobile Number" onChange = {this.InputChange}/>
                </div>
                <div class="col-8 mt-4">
                <input type="text" class="form-control w-100" placeholder="Email Address" onChange = {this.InputChange}/>
                </div>
                <div class="col-8 mt-4">
                <input type="password" class="form-control w-100" placeholder="Password" onChange = {this.InputChange}/>
                </div>
                <div class="col-8 mt-4">
                <label> Birthday</label>
                <input type="date" class="form-control w-100" placeholder="Date Of Birth" onChange = {this.InputChange}/>
                </div>
                <div class="col-8 mt-4">
                <div>
                        <input type="radio"
                            value="Female"
                            checked={this.state.Gender === "Female"}
                            onChange={this.radioChange} />Female

                        <input type="radio"
                            value="Male"
                            checked={this.state.Gender === "Male"}
                            onChange={this.radioChange}/>Male

                        <input type="radio"
                            value="Custom"
                            checked={this.state.Gender === "Custom"}
                            onChange={this.radioChange}/>Male
                    </div> 
                    </div>
                <div class=" col-12 mt-4">
                <button type="button" class="btn btn-info">SignUp</button>

                </div>
            </div>

       </form>
             </div>
         )
     }

 }
 export default SignUpUser
