import React, { Component } from 'react'
import axios from 'axios'
import Navbar from './Navbar'

export default class Dashboard extends Component {
    constructor(props) {
        super(props)
        this.state = {
            allCategories: [],
            city: '',
            marrital_status: "",
            Education: "",
            postimageLink: '',
            image: '',
            user_id: '',
            first_name: "",
            last_name:"",
            tokenId: localStorage.getItem('token')

        }
    }
    componentDidMount() {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data.user_id)
                this.setState({
                    user_id: response.data.user_id,
                    first_name: response.data.first_name,
                    last_name: response.data.last_name
                })
            })
            .catch((err) => alert(err))
    }

    inputChange = (e) => {
        this.setState({
            postImageLink: e.target.files[0]
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onSubmit = (e) => {
        e.preventDefault()
        console.log(this.state)
        const formData = new FormData();
        formData.append('postImageLink', this.state.postImageLink)
        axios.post("http://127.0.0.1:5000/profile/update", formData, {
            headers: {
                user_id: this.state.user_id,
                first_name: this.state.first_name,
                Education: this.state.Education,
                marrital_status: this.state.marrital_status,
                city: this.state.city,
            }

        })

            .then(response => {
                console.log(response.data.path)
                this.setState({
                    image: response.data.path
                })
                this.props.history.push('/Dashboard')
            })
            .catch(error => {
                console.log(error)
            })

    }


    render() {
        // console.log(this.state)
        return (
            <div>
                <Navbar />
                <form onSubmit={this.onSubmit}>
                    <h1 className="offset-3 mt-3 text-primary" >update User</h1>
                    <h4 className="offset-3 mt-2">city</h4>
                    <input type="text" className="offset-3 w-25 mt-2" name="city" onChange={this.onChange}></input>
                    <h4 className="offset-3 mt-2">Education</h4>
                    <input className="offset-3 w-25 mt-2" type="text" name="Education" onChange={this.onChange}></input>
                    <h4 className="offset-3 mt-2">marrital_status</h4>
                    <input className="offset-3 w-25 mt-2" type="text" name="marrital_status" onChange={this.onChange}></input>
                    <br></br>
                    <div className="form-group" style={{ "marginLeft": "500px" }}>
                        <label>Choose Picture</label>
                        <input type="file" className="form-control-file" name="postImageLink" onChange={this.inputChange} />
                    </div>
                    <button className="btn btn-primary offset-3 w-25 mt-2 btn-lg">POST</button>
                </form>
            </div>
        )
    }
}
