import React, { Component } from 'react'
import { Link } from "react-router-dom";
import axios from 'axios'
import Navbar from './Navbar';

export default class ShowAllUsers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user_id: "",
            allUser: [],
            tokenId: localStorage.getItem("token"),
            searchData:[]

        }
    }
    componentDidMount = () => {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data.user_id)
                this.setState({
                    user_id: response.data.user_id,
                    userDetails: response.data
                })

                //Show All Users
                axios.get("http://127.0.0.1:5000/ShowAll_user1", {
                    headers: {
                        user_id: response.data.user_id
                    }
                })
                    .then((response) => {
                        console.log(response.data)
                        this.setState({
                            allUser: response.data
                        })
                    })
                    .catch((err) => alert(err))
            })


    }
    render() {
        let allUser = this.state.allUser.map(e => {
            return (
                <div className = "card w-50 mt-5">
                    <img  src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "150px", height: "150px" }} className="  rounded-circle" alt = "image"></img>                    
                    <div className = "h4 offset-4" style = {{marginTop:"-140px"}}>{e.first_name} {e.last_name} </div>
                    <div className = "h4 offset-4" >{e.profile.city}</div>
                    <Link to={`/ViewUsers/${e._id.$oid}`}><button className="btn btn-primary mt-5 offset-4">View Profile</button></Link>
                </div>
            )
        })
        return (
            <div>
                <div>
                <form>
                <div class="form-group">
                    <label for="exampleInputEmail1">Email address</label>
                    <input type="email" class="form-control w-2"  />
                    <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                </div>
                </form>
                </div>
                <Navbar />
                <div className="offset-4">
                    {allUser}
                </div>
            </div>
        )
    }
}
