import React, { Component } from 'react';
import axios from 'axios';
import { Button } from '@material-ui/core';


export class Pegination extends Component {
    constructor(props){
        super(props)
        this.state = {
            story : [],
            per_page :""
        }
    }
//    componentDidMount = (page = 1) =>{
//     axios.get(`http://127.0.0.1:5000/read/story?page=${page}`,{
//         per_page : this.state.per_page
//     })
//     .then(res=>{
//         console.log(res.data)
//     })
//    }
   handleChange = (a) =>{
    console.log(this.state)
       this.setState({
           per_page: a.target.value
       })

   }
   onSubmit = (page = 1,e) =>{
    e.preventDefault()  
    let data ={
        per_page : this.state.per_page
    } 
    axios.get(`http://127.0.0.1:5000/read/story?page=${page}`,data)
    .then(res=>{
        console.log(res.data)
    })
    .catch(error=>{
        alert(error)

    })
   }
    render() {

        return (
            <div>
            <form onSubmit = {this.onSubmit}>
            <select class="custom-select" value = {this.state.per_page}  onChange = {this.handleChange}>
                <option selected>Open this select menu</option>
                <option value="1">One</option>
                <option value="2">Two</option>
                <option value="3">Three</option>
                </select>
        <Button  variant="contained" color="secondary" type="submit" className="ml-1" > Submit </Button>

        </form> 
            </div>
        )
    }
}

export default Pegination
