import React, { Component } from 'react';
import Navbar from './Navbar';
import {TextField,Button  } from '@material-ui/core';
import axios from 'axios'


export class Login extends Component {
    constructor(props){
        super(props)
        this.state = {
            email : '',
            password : ""
        }


    }
    handleInput = (e) =>{

        this.setState({
            [e.target.name]: e.target.value
        })
    }
    onSubmit = (e) => {
        e.preventDefault();
        var user_details = {
            email: this.state.email,
            password: this.state.password
        }
        axios.post('http://127.0.0.1:5000/users/login', user_details)
        .then(response => {
            // console.log(response.data)
            console.log(response.data.token)
            if(response.data.token){
                localStorage.setItem("token", response.data.token)
                setUserData(response.data.token)
            }else{
                alert("Wrong email or password")
            }
        })
        .catch(error => {
            console.log(error)
        })

        let setUserData = (tokenId) =>{
            axios.get("http://127.0.0.1:5000/get-user-token", {
                headers: {
                    Authorization: "Bearer " + tokenId,
                    'Content-Type': 'application/json'
                }
            })
            .then(response => {
                console.log(response.data)
                localStorage.setItem("userData",JSON.stringify(response.data));
                this.props.history.push("/Updateuser")
            })
            .catch(error => {
                console.log(error)
            })
        }
    }


    render() {
        console.log(this.state)
        return (
    <div>

    <h3  className="text-info mt-5 mb-5"  style = {{textAlign:"center"}}> Login here</h3>

    <form  onSubmit = {this.onSubmit}  className =" offset-5 ">
    {/* <Paper className =" w-50 mt-5" style={{height:"200px"}}> */}
        <div>
        <TextField
        className = "ml-2 mb-3"
          label="Enetr Email"
          type="text"
          variant="filled"
          name = "email"
          onChange={this.handleInput}
        />
        </div>
        <div>
        <TextField
        className = "ml-2 mb-3"
          label="Password"
          type="password"
          variant="filled"
          name = "password"
          onChange={this.handleInput}
        />
        </div>
        <Button variant="contained" type = "submit" className = "ml-5" color="secondary">
        Submit
        </Button>
    {/* </Paper> */}
        </form>
     </div>
        )
    }
}

export default Login
