import React from 'react'
import { Redirect } from 'react-router-dom'
import Axios from 'axios'


class ProfilePic extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            photo : '',
            status : false,
            token : window.localStorage.getItem('token')
        }
    }

    handlePhotoChange = (e) => {
        this.setState({
            photo : e.target.files[0],
        })
    }

    handleClick = (e) => {
        e.preventDefault()
        const formData = new FormData()
        formData.append('image', this.state.photo)
        Axios.post(`http://127.0.0.1:5000/displaypic`, formData, {
            headers : {
                Authorization : "Bearer " + this.state.token,
                "Content-type" : "application/json"
            }
        })
        .then(res => {
            console.log(res.data)
            this.setState({
                status : true
            })
        })
    }

    render() {
        return(
            <div className = 'container'>
                <form>
                    <div className="form-group text-center container mt-5">
                        <label htmlFor="exampleFormControlFile1">Upload your picture here</label>
                        <input type="file" name='photo' onChange={this.handlePhotoChange} className="form-control-file" id="exampleFormControlFile1"/>
                    </div>
                </form>
                <button onClick={this.handleClick} className='btn btn-primary'>upload</button>
                {this.state.status ? <Redirect to='/dashboard'/> : null}
            </div>
        )
    }
}

export default ProfilePic
