import React from "react"
import axios from "axios";
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import queryString from "query-string";
import Createstory from './Createstory'


class Story extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allstory: [],
            allDAta :[],
            Comments_all: [],
            per_page: 0,
            // change_page: 
            // {
			//  page: 1
	        //     },
            // total_page: 0,
            tokenId: window.localStorage.getItem("token"),
            currentPage: 1,
            allData:[],
            per_page:5

        }

    }
    componentDidMount = (page = 1) => {
        axios.get(`http://127.0.0.1:5000/read_userStoryall?page=${page}`)
            .then(res => {
              console.log(res.data.data)
                this.setState({
                    allstory: res.data.data,
                    total_pages: res.data.total_pages
                })
                // console.log(res.data[0].comment)
                // alert("helllo")
            })
            .catch((err) => alert(err))

        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                  allDAta : response.data
                })
            })
            // .catch((err) => alert(err))
    }
    Deleteblog = (story_id) => {
        axios.delete('http://127.0.0.1:5000/delete-story/' + story_id, {
            headers: {
                user_id: this.state.allDAta.user_id
            }
        })
            .then((res) => {
                console.log(res.data)
                this.props.history.push(`/Story/${this.state.user_id}`)
                window.location.reload(false)
            })
            .catch((error) => alert(error))
    }
    pagination = (pageNo) => {
		let updatePage = this.state.change_page
		updatePage.page = pageNo
		this.setState(
			{
				change_page: updatePage
			},
			() => {
				this.props.history.push(`?${queryString.stringify(updatePage)}`);
			}
        );
		this.componentDidMount(this.page = pageNo);
	};

    render() {
        const per_page = this.state.per_page
        const pageCount = 3  // How many button you want
        let currentPage = this.state.currentPage
        let totalLength = this.state.allData.length
        let start = (currentPage-1)*Number(per_page)
        let end = start + Number(per_page)
        let slicedData = this.state.allData.slice(start,end)

        let all_blog = this.state.allstory.reverse().map((e) => {
            if (this.state.allDAta.user_id === e.user_id) {
                return ( 
                  <div class="card mb-3 w-50 offset-3">
                  <div class="card-body">
                  <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>
                   <p > name : {e.first_name} {e.last_name} </p>
                   <p > story : {e.story_content}</p>
                   <img  className ="w-100" style={{ height: "250px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`} alt = "post"></img>
                    <p class="card-text"></p>
                    <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    <Link to={`/Updatestory/${e.story_id}`}><button type="submit" className="btn btn-primary">Update story </button></Link>
                    <button type="submit" className="btn btn-danger ml-1" onClick={() => this.Deleteblog(e.story_id)}> Delete story </button>
                    </div>


                </div>

                )
            }
            else {
                return (
                  <div class="card mb-3 w-50 offset-4">
                  <div class="card-body">
                  <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>
                   <p > name: {e.first_name}  {e.last_name} </p>
                   <p > story : {e.story_content}</p>
                   <img  className ="w-100" style={{ height: "250px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`} alt = "post"></img>
                    <p class="card-text"></p>
                    <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                    <Link to={`/Comment/${e.story_id}`}><button type="submit" className="btn btn-primary offset-3"> Comment </button></Link>

                    </div>


                </div>
                )

            }
        })
        return (
            <div>
                <Pagination
          total={totalLength}
          limit={per_page}
          pageCount={pageCount}
          currentPage={currentPage}
        >
          {({
            pages,
            currentPage,
            hasNextPage,
            hasPreviousPage,
            previousPage,
            nextPage,
            totalPages,
            getPageItemProps
          }) => (
            <div>
              <button className="btn btn-info"
                {...getPageItemProps({
                  pageValue: 1,
                  onPageChange: this.handlePageChange
                })}
              >
                first
              </button>

              {hasPreviousPage && (
                <button className="btn btn-info"
                  {...getPageItemProps({
                    pageValue: previousPage,
                    onPageChange: this.handlePageChange
                  })}
                >
                  {"<"}
                </button>
              )}

              {pages.map(page => {
                let activePage = null;
                if (currentPage === page) {
                  activePage = { backgroundColor: "blue" };
                }
                return (
                  <button className="btn btn-primary"
                    {...getPageItemProps({
                      pageValue: page,
                      key: page,
                      style: activePage,
                      onPageChange: this.handlePageChange
                    })}
                  >
                    {page}
                  </button>
                );
              })}

              {hasNextPage && (
                <button className="btn btn-info"
                  {...getPageItemProps({
                    pageValue: nextPage,
                    onPageChange: this.handlePageChange
                  })}
                >
                  {">"}
                </button>
              )}

              <button className="btn btn-info"
                {...getPageItemProps({
                  pageValue: totalPages,
                  onPageChange: this.handlePageChange
                })}
              >
                last
              </button>
            </div>
          )}
        </Pagination>
                <Navbar props={this.props} />
                <div className = "mb-3">
                <Createstory />
                </div>
                {all_blog}
                <div className="offset-5 mt-3 mb-5">
                    {renderPageNumbers}
                </div>
            </div>
        )
    }

}

export default Story
