import React from "react"
import axios from "axios";
// import { Link } from 'react-router-dom';
import Navbar from './Navbar'


class Story extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allstory: [],
            user_details:[],
            per_page: 0,
            page: 1,
            total_page: 0,
            tokenId: window.localStorage.getItem("token")

        }

    }
    componentDidMount = (page = 1) => {
        axios.get(`http://127.0.0.1:5000/users_twite?page=${page}`)
            .then(res => {
                this.setState({
                    allTwite: res.data.data,
                    total_pages: res.data.total_pages,
                })
            })
            .catch((err) => alert(err))
    }
    pagination = (pageNo) => {
        this.setState({
            page: pageNo
        })
        this.componentDidMount(this.page = pageNo)
    }

    render() {
        const pageNumbers = [];
        for (let i = 1; i <= this.state.total_pages; i++) {
            pageNumbers.push(i);
        }
        const renderPageNumbers = pageNumbers.map(number => {
            return (
                <button className="btn btn-secondary ml-2" onClick={() => this.pagination(number)}>{number}</button>
            );
        });
        let all_Twite = this.state.allTwite.reverse().map((e) => {
            return (
                <div className="card w-25 mb-4 offset-3 text-center" >
                    <p>  {e.Twite_content}</p>
                    <p>  {e.user_id}</p>
                    <p>  {e.name}</p>
                    <img class="w-200" style={{ width: "300px", height: "200px" }} src={`http://127.0.0.1:5000/${e.postImageLink}`} alt="post"></img>
                    <div>
                    </div>
                </div>
            )
        })
        return (
            <div>
                <Navbar />
                <h2 className="offset-4">All twite</h2>
                {all_Twite}
                <div className="offset-4 mt-3 mb-5">
                    {renderPageNumbers}
                </div>
            </div>
        )
    }

}

export default Story
