import React, { Component } from 'react';
import axios from 'axios';



export class Pegination extends Component {
    constructor(props){
        super(props)
        this.state = {
            story : [],
            per_page :""
        }
    }
   componentDidMount = (page = 1) =>{
    axios.get(`http://127.0.0.1:5000/read/story?page=${page}`,{
        per_page : this.state.per_page
    })
    .then(res=>{
        console.log(res.data)
    })
   }
   handleInput =(e)=>{
    e.preventDefault()
    this.setState({
     [e.target.name]:e.target.value
    })
    console.log(this.state.per_page)
  }
    render() {

        return (
            <div>
              <select class="custom-select" id="inputGroupSelect01" name="per_page" onChange ={this.handleInput}>
                <option selected>Choose...</option>
                <option value="5">5</option>
                <option value="10">10</option>
                <option value="15">15</option>
              </select>
        {/* <Button  variant="contained" color="secondary" type="submit" className="ml-1" > Submit </Button> */}
            </div>
        )
    }
}

export default Pegination
