import React, { Component } from 'react';
import axios from 'axios';
import Select from '@material-ui/core/Select';
import { Button } from '@material-ui/core';


export class Pegination extends Component {
    constructor(props){
        super(props)
        this.state = {
            story : [],
            per_page :""
        }
    }
//    componentDidMount = (page = 1) =>{
