import React, { Component } from 'react'
// import { Link } from "react-router-dom";
import axios from 'axios'
import Navbar from './Navbar'

export default class ViewUsers extends Component {
   constructor(props){
       super(props)
       this.state = {
        user_id: "",
        allUser: [],
        tokenId: localStorage.getItem("token")


       }
   }
   componentDidMount = () => {
    axios.get("http://127.0.0.1:5000/get-user-token", {
        headers: {
            Authorization: "Bearer " + this.state.tokenId,
            'Content-Type': 'application/json'
        }
    })
        .then(response => {
            console.log(response.data.user_id)
            this.setState({
                user_id: response.data.user_id,
                userDetails: response.data
            })

            //Show All Users
            axios.get("http://127.0.0.1:5000/read_user", {
                headers: {
                    user_id: this.props.match.params.user_id
                }
            })
                .then((response) => {
                    console.log(response.data)
                    this.setState({
                        allUser: response.data
                    })
                })
                .catch((err) => alert(err))
        })
}

onConnect =()=>{
    var connectionDetails = {
        reciever_user_id:this.props.match.params.user_id,
        current_userid:this.state.allUser.user_id,
    }
    axios.post("http://127.0.0.1:5000/user/request",connectionDetails)
            .then((response) => {
                console.log(response.data)
                window.location.reload(false);
            })
            .catch((err) => alert(err))
}   

   render(){
       let userDetails = this.state.allUser.map((a)=>{
           return(
               <div className = "container">
               <div className ="card w-50 mt-4">
                  <img  src={`http://127.0.0.1:5000/${a.postImageLink}`} style={{ width: "50px", height: "50px" }} className=" offset-3 rounded-circle" alt = "image"></img>
                   <p className = "offset-3 "> name:{a.first_name}</p>
                   <p className = "offset-3 "> city:{a.profile.city}</p>
                   <p className = "offset-3 "> marital_status:{a.profile.marital_status}</p>
                   <p className = "offset-3 "> Education:{a.profile.Education}</p>
                   <p className = "offset-3 "> Gender:{a.Gender}</p>
                   <p className = "offset-3 "> DateOfBirth:{a.DateOfBirth}</p>
                   <div>
                   <button className="btn btn-success offset-3" >addfriend</button>
                   <button className="btn btn-danger offset-3" >delete</button>
                   </div>
               </div>
               </div>
           )

       })
       return(
           <div>
               <Navbar />
               {userDetails}
           </div>
       )
   }

}
