import React, { Component } from 'react';
import axios from 'axios';



export class Pegination extends Component {
    constructor(props){
        super(props)
        this.state = {
            story : [],
            per_page :""
        }
    }
   componentDidMount = (page = 1) =>{
    axios.get(`http://127.0.0.1:5000/read/story?page=${page}`,{
        per_page : this.state.per_page
    })
    .then(res=>{
        console.log(res.data)
    })
   }
   handleChange = (a) =>{
    console.log(this.state)
       this.setState({
           [a.target.name]: a.target.value
       })

   }

    render() {

        return (
            <div>
            <select class="custom-select"  name = "per_page" onChange = {this.handleChange}>
                <option selected>Open this select menu</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                </select>
        {/* <Button  variant="contained" color="secondary" type="submit" className="ml-1" > Submit </Button> */}
            </div>
        )
    }
}

export default Pegination
