import React, { Component } from 'react'
import axios from 'axios'
import Navbar from './Navbar'



export default class Createstory extends Component {
    constructor(props) {
        super(props)
        this.state = {
            allCategories: [],
            story_content: "",
            user_id: '',
            postimageLink1: '',
            allData:[],
            tokenId: localStorage.getItem("token")

        }
    }

    componentDidMount() {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    allData : response.data
                })
            })
            .catch((err) => alert(err))

    }
    inputChange = (e) => {
        this.setState({
            postImageLink: e.target.files[0]
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onSubmit = (e) => {
        e.preventDefault()
        const formData = new FormData();
        console.log(this.state)
        formData.append('postImageLink1', this.state.postImageLink)
        axios.post("http://127.0.0.1:5000/create_story", formData, {
            headers: {
                Twite_content: this.state.Twite_content,
                user_id: this.state.user_id,
                name: this.state.name

            }
        })

            .then(response => {
                console.log(response.data.path)
                this.setState({
                    image: response.data.path
                })
                this.props.history.push('/ShowAlltwite')
            })
            .catch(error => {
                console.log(error)
            })

    }


    render() {
        return (
            <div>
                <Navbar />
                <form onSubmit={this.onSubmit}>
                    <h2 className=" text-center mt-3 text-primary " >Create Twite</h2>
                    <h4 className="offset-3 mt-2">Content</h4>
                    <textarea className="offset-3 mt-2" rows="8" cols="80" name="Twite_content" onChange={this.onChange}></textarea>
                    <br></br>

                    <div className="form-group" style={{ "marginLeft": "900px", "marginTop": "45px" }}>
                        <label>Choose Picture</label>
                        <input type="file" className="form-control-file" name="postImageLink" onChange={this.inputChange} />
                    </div>
                    <button className="btn btn-primary offset-4 w-25 mt-2 btn-lg">Crate Twite</button>
                </form>
            </div>
        )
    }
}
