import React from "react"
import axios from "axios";
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import queryString from "query-string";


class Story extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allstory: [],
            allDAta :[],
            Comments_all: [],
            per_page: 0,
            change_page: 
            {
			 page: 1
	            },
            total_page: 0,
            tokenId: window.localStorage.getItem("token")

        }

    }
    componentDidMount = (page = 1) => {
        axios.get(`http://127.0.0.1:5000/read_userStoryall?page=${page}`)
            .then(res => {
              console.log(res.data.data)
                this.setState({
                    allstory: res.data.data,
                    total_pages: res.data.total_pages
                })
                // console.log(res.data[0].comment)
                // alert("helllo")
            })
            .catch((err) => alert(err))

        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                  allDAta : response.data
                })
            })
            // .catch((err) => alert(err))
    }
    // Deleteblog = (blog_id) => {
    //     axios.delete('http://127.0.0.1:5000/delete-blog/' + blog_id, {
    //         headers: {
    //             user_id: this.state.user_id
    //         }
    //     })
    //         .then((res) => {
    //             console.log(res.data)
    //             this.props.history.push(`/Blog/${this.state.user_id}`)
    //             window.location.reload(false)
    //         })
    //         .catch((error) => alert(error))
    // }
    pagination = (pageNo) => {
		let updatePage = this.state.change_page
		updatePage.page = pageNo
		this.setState(
			{
				change_page: updatePage
			},
			() => {
				this.props.history.push(`?${queryString.stringify(updatePage)}`);
			}
        );
		this.componentDidMount(this.page = pageNo);
	};

    render() {
      console.log(this.state.allstory)
        const pageNumbers = [];
        for (let i = 1; i <= this.state.total_pages; i++) {
            pageNumbers.push(i);
        }
        const renderPageNumbers = pageNumbers.map(number => {
            return (
                <button className="btn btn-secondary ml-2" onClick={() => this.pagination(number)}>{number}</button>
            );
        });
        let all_blog = this.state.allstory.reverse().map((e) => {
            if (this.state.allDAta.user_id === e.user_id) {
                return (
                                    <div class="card mb-3" style="max-width: 540px;">
                    <div class="row no-gutters">
                      <div class="col-md-4">
                      <img class="w-200" style={{ height: "200px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`} alt = "post"></img>
                      </div>
                      <div class="col-md-8">
                        <div class="card-body">
                        <p className = "ml-5" >name: {e.first_name}  <p>{e.last_name}</p> </p>
                        <hp > story : {e.story_content}</hp>
                      <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>

                        </div>
                      </div>
                    </div>
                    <Link to={`/Comment/${e.story_id}`}><button type="submit" className="btn btn-info"> Show More </button></Link>
                      <button type="submit" className="btn btn-danger ml-5" onClick={() => this.Deleteblog(e.story_id)}> Delete story </button>
                      <Link to={`/Updatestory/${e.story_id}`}><button type="submit" className="btn btn-primary ml-5">Update story </button></Link>

                  </div>
                )
            }
            else {
                return (
                    <div className="card w-50 mb-4 offset-3 text-center " >

                        <img class="w-100" style={{ height: "300px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`}></img>
                        <h4> content : {e.story_content}</h4>
                        <h4>name:{e.first_name} </h4>
                      <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>
                       {/* <h4>user_id:{this.state.allDAta.first_name} </h4> */}
                        <div>
                            <Link to={`/Comment/${e.story_id}`}><button type="submit" className="btn btn-info"> Show More </button></Link>
                        </div>
                    </div>
                )

            }
        })
        return (
            <div>
                <Navbar />
                <h2 className="offset-5">story</h2>
                {all_blog}
                <div className="offset-5 mt-3 mb-5">
                    {renderPageNumbers}
                </div>
            </div>
        )
    }

}

export default Story
