import Navbar from './Navbar';
import {TextField,Paper,Button  } from '@material-ui/core';


export class Login extends Component {
    constructor(props){
        super(props)
        this.state = {
            email : '',
            password : ""
        }


    }
    handleInput = (e) =>{

        this.setState({
            [e.target.name]: e.target.value
        })
    }
    handleSubmit = (user) =>{
        user.preventDefault()
        console.log(this.state.value)
    }


    render() {
        console.log(this.state)
        return (
    <div>

            <h3  className="text-info mt-5 mb-5"  style = {{textAlign:"center"}}> Login here</h3>

    <form  onSubmit={this.onSubmit}  className =" offset-5 " noValidate autoComplete="off">
    {/* <Paper className =" w-50 mt-5" style={{height:"200px"}}> */}
