import React from "react"
import axios from "axios";
import { Link } from 'react-router-dom';
import Navbar from './Navbar'


class Userstory extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allStory: [],
            user_id: '',
            Comments_all: [],
            tokenId: window.localStorage.getItem("token")

        }

    }

    componentDidMount = (e) => {
        axios.get('http://127.0.0.1:5000/read_userStory/' + this.state.user_id)
            .then(res => {
                this.setState({
                    allStory: res.data,
                })
                console.log(res.data)
            })
            .catch((err) => alert(err))
    }
    // DeleteStory = (story_id) => {
    //     axios.delete('http://127.0.0.1:5000/delete-story/' + story_id, {
    //         headers: {
    //             user_id: this.state.user_id
    //         }
    //     })
    //         .then((res) => {
    //             console.log(res.data)
    //             // this.props.history.push(`/Blog/${this.state.user_id}`)
    //             window.location.reload(false)
    //         })
    //         .catch((error) => alert(error))
    // }

    render() {
        let all_story = this.state.allStory.reverse().map((e) => {

                return (
                    <div>
                    <div>
                        <p>{e.fir}</p>
                    </div>
                    <div className="card w-50 mb-4 offset-3 text-center" >
                        <img class="w-100" style={{ height: "300px" }} src={`http://127.0.0.1:5000/${e.postImageLink1}`} alt=""></img>
                        <p> {e.story_content}</p>
                        <div>
                            {/* <Link to={`/Comment/${e.story_id}`}><button type="submit" className="btn btn-info"> Comment </button></Link> */}
                            <button type="submit" className="btn btn-danger ml-5" onClick={() => this.DeleteStory(e.story_id)}> Delete Blog </button>
                            <Link to={`/Edit/${e.story_id}`}><button type="submit" className="btn btn-primary ml-5"> Edit Blog </button></Link>

                        </div>
                    </div>
                    </div>
                )


        })
        return (
            <div>
                <Navbar />
                <h2 className="offset-5"> story </h2>
                {all_story}


            </div>
        )
    }

}

export default Userstory
