import React from "react"
import axios from "axios";
import { Link } from 'react-router-dom';
import Navbar from './Navbar'


class ShowAllUsers extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            allUser: [],
            User_all: [],
            tokenId: window.localStorage.getItem("token")

        }

    }
    componentDidMount = (e) => {
        axios.get('http://127.0.0.1:5000/read')
            .then(res => {
                this.setState({
                    allUser: res.data,
                })
            })
            .catch((err) => alert(err))

        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    user_id: response.data.user_id
                })
            })
            .catch((err) => alert(err))
    }


    render() {
        let all_User = this.state.allUser.reverse().map((e) => {
            //   console.log('in map', e.comment[0].comment)
            return (
                <div className=" card w-50 mb-4 offset-3 text-center" >
                    <h4> name : {e.name}</h4>
                    <img style={{ width: "150px", height: "150px" }} className="rounded-circle offset-5" src={`http://127.0.0.1:5000/${e.postImageLink}`}></img>
                    <Link to={`/ShowblogUser/${e.user_id}`}><button className="btn btn-success offset-1 w-25 mt-2 btn-lg"> Show Blog</button></Link>

                </div>
            )

        })
        return (
            <div>
                <Navbar />
                <h2 className="offset-5 text-info mt-5" style={{ borderBottom: "2px solid green", display: "inline" }}>User Page</h2>
                {all_User}


            </div>
        )
    }

}

export default ShowAllUsers
