import React, { Component } from 'react'
import axios from 'axios'
import Navbar from './Navbar'



export default class Createblog extends Component {
    constructor(props) {
        super(props)
        this.state = {
            postimageLink: '',
            image: '',
            tokenId: localStorage.getItem("token")

        }
    }

    componentDidMount() {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    user_id: response.data.user_id
                })
            })
            .catch((err) => alert(err))

    }
    inputChange = (e) => {
        this.setState({
            postImageLink: e.target.files[0]
        })
    }

    onSubmit = (e) => {
        e.preventDefault()
        const formData = new FormData();
        console.log(this.state)
        formData.append('postImageLink', this.state.postImageLink)
        axios.post("http://127.0.0.1:5000/create_blog", formData)
            .then(response => {
                console.log(response.data.path)
                this.setState({
                    image: response.data.path
                })
                this.props.history.push('/Dashboard')
            })
            .catch(error => {
                console.log(error)
            })

    }   
    render() {
        return (
            <div>
                <Navbar />
                <form onSubmit = {this.onSubmit}>
                    <h2 className=" text-center mt-3 text-primary " >Upload Picture</h2>
                    <div className="form-group" style={{ "marginLeft": "900px", "marginTop": "-65px" }}>
                        <label>Choose Picture</label>
                        <input type="file" className="form-control-file" name="postImageLink" onChange={this.inputChange} />
                    </div>  
                    <button className="btn btn-primary offset-4 w-25 mt-2 btn-lg">POST BLOG</button>
                </form>
            </div>
        )
    }
}
