import React from "react"
// import axios from "axios";
// import { Link } from 'react-router-dom';
import Navbar from './Navbar'
import PhotUpload from './Photoupload'


class Dashboard extends React.Component {
    constructor(props) {
        super(props)
        this.state = {

        }

    }
    componentDidMount() {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    user_id: response.data.user_id
                })
            })
            .catch((err) => alert(err))

    }
    inputChange = (e) => {
        this.setState({
            postImageLink: e.target.files[0]
        })
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    onSubmit = (e) => {
        e.preventDefault()
        console.log(this.state)
        var editData ={
            blog_title: this.state.blog_title,
            category: this.state.category,
            blog_content: this.state.blog_content
        }
        axios.post("http://127.0.0.1:5000/update_blog/" + this.props.match.params.blog_id, editData)

            .then(response => {
                console.log(response.data.path)
                this.setState({
                    image: response.data.path
                })
                this.props.history.push('/Blog')
            })
            .catch(error => {
                console.log(error)
            })

    }
   render(){
       return (
           <div>
               <Navbar  props = {this.props}/>
               <PhotUpload />
               <form onSubmit={this.onSubmit}>
                    <h2 className=" text-center mt-3 text-primary " >Update Blog</h2>
                    <h4 className="offset-3 mt-2">Title</h4>
                    <textarea className="offset-3 mt-2" rows="2" cols="80" name="blog_title" onChange={this.onChange}></textarea>
                    <h4 className="offset-3 mt-2">Content</h4>
                    <textarea className="offset-3 mt-2" rows="8" cols="80" name="blog_content" onChange={this.onChange}></textarea>
                    <br></br>
                    <h4 className="offset-3 mt-2"> Blog Category</h4>
                    <textarea className="offset-3 mt-2" rows="2" cols="80" name="category" onChange={this.onChange}></textarea>
                    <button className="btn btn-primary offset-4 w-25 mt-2 btn-lg"> Update Blog</button>
                </form>

           </div>
       )
   }
}

export default Dashboard
