import React from 'react'
import {AppBar,Toolbar,Typography }from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import classes from './Home.css';
import LoginUser from './LoginUser'
import SignUpUser from './SignpUser';
import axios from 'axios';
import classes1 from './Navbar.css';
import InputBase from '@material-ui/core/InputBase';
import Menu from '@material-ui/core/Menu';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MailIcon from '@material-ui/icons/Mail';
import NotificationsIcon from '@material-ui/icons/Notifications';
import MoreIcon from '@material-ui/icons/MoreVert';


class Navbar extends React.Component {
    constructor(props){
        super(props)
        this.state={
            tokenId: localStorage.getItem("token"),
            user_id:''

        }
    }
    componentDidMount() {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data)
                this.setState({
                    user_id: response.data.user_id
                })
            })
            .catch((err) => alert(err))

    }
    render(){
        const [anchorEl, setAnchorEl] = React.useState(null);
             const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState(null);

             const isMenuOpen = Boolean(anchorEl);
             const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

             const handleProfileMenuOpen = event => {
               setAnchorEl(event.currentTarget)
             };

             const handleMobileMenuClose = () => {
               setMobileMoreAnchorEl(null)
             };

             const handleMenuClose = () => {
               setAnchorEl(null);
               handleMobileMenuClose();
             };

             const handleMobileMenuOpen = event => {
               setMobileMoreAnchorEl(event.currentTarget);
             };
             const menuId = 'primary-search-account-menu';
             const renderMenu = (
                <Menu
                  anchorEl={anchorEl}
                  anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                  id={menuId}
                  keepMounted
                  transformOrigin={{ vertical: 'top', horizontal: 'right' }}
                  open={isMenuOpen}
                  onClose={handleMenuClose}
                >
                  <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
                  <MenuItem onClick={handleMenuClose}>My account</MenuItem>
                </Menu>
              );
             const mobileMenuId = 'primary-search-account-menu-mobile';
             const renderMobileMenu = (
               <Menu
                 anchorEl={mobileMoreAnchorEl}
                 anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
                 id={mobileMenuId}
                 keepMounted
                 transformOrigin={{ vertical: 'top', horizontal: 'right' }}
                 open={isMobileMenuOpen}
                 onClose={handleMobileMenuClose}
               >
                 <MenuItem>
                   <IconButton aria-label="show 4 new mails" color="inherit">
                     <Badge badgeContent={4} color="secondary">
                       <MailIcon />
                     </Badge>
                   </IconButton>
                   <p>Messages</p>
                 </MenuItem>
                 <MenuItem>
                   <IconButton aria-label="show 11 new notifications" color="inherit">
                     <Badge badgeContent={11} color="secondary">
                       <NotificationsIcon />
                     </Badge>
                   </IconButton>
                   <p>Notifications</p>
                 </MenuItem>
                 <MenuItem onClick={handleProfileMenuOpen}>
                   <IconButton
                     aria-label="account of current user"
                     aria-controls="primary-search-account-menu"
                     aria-haspopup="true"
                     color="inherit"
                   >
                     <AccountCircle />
                   </IconButton>
                   <p>Profile</p>
                 </MenuItem>
               </Menu>
             );

        return(

            <div>
        {this.state.tokenId? 
               <div className={classes1.grow}>
                 <AppBar position="static">
                   <Toolbar>
                     <IconButton
                       edge="start"
                       className={classes1.menuButton}
                       color="inherit"
                       aria-label="open drawer"
                     >
                       <MenuIcon />
                     </IconButton>
                     <Typography className={classes1.title} variant="h6" noWrap>
                       Material-UI
                     </Typography>
                     <div className={classes1.search}>
                       <div className={classes1.searchIcon}>
                         <SearchIcon />
                       </div>
                       <InputBase
                         placeholder="Search…"
                         classes={{
                           root: classes1.inputRoot,
                           input: classes1.inputInput,
                         }}
                         inputProps={{ 'aria-label': 'search' }}
                       />
                     </div>
                     <div className={classes1.grow} />
                     <div className={classes1.sectionDesktop}>
                       <IconButton aria-label="show 4 new mails" color="inherit">
                         <Badge badgeContent={4} color="secondary">
                           <MailIcon />
                         </Badge>
                       </IconButton>
                       <IconButton aria-label="show 17 new notifications" color="inherit">
                         <Badge badgeContent={17} color="secondary">
                           <NotificationsIcon />
                         </Badge>
                       </IconButton>
                       <IconButton
                         edge="end"
                         aria-label="account of current user"
                         aria-controls={menuId}
                         aria-haspopup="true"
                         onClick={handleProfileMenuOpen}
                         color="inherit"
                       >
                         <AccountCircle />
                       </IconButton>
                     </div>
                     <div className={classes.sectionMobile}>
                       <IconButton
                         aria-label="show more"
                         aria-controls={mobileMenuId}
                         aria-haspopup="true"
                         onClick={handleMobileMenuOpen}
                         color="inherit"
                       >
                         <MoreIcon />
                       </IconButton>
                     </div>
                   </Toolbar>
                 </AppBar>
                 {renderMobileMenu}
                 {renderMenu}
               </div>
         :
                <div className={classes1.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes1.menuButton} color="inherit" aria-label="menu">
            {/* <MenuIcon /> */}
          </IconButton>
          <Typography variant="h3"  style ={{marginLeft:"200px"}} className={classes1.title}>
           facebook
          </Typography>
          <LoginUser props={this.props}/>
        </Toolbar>
      </AppBar>
      <div style ={{marginLeft:"800px"}}>
          <SignUpUser />
      </div>
    </div>
 }
            </div>
        )


    }
}
export default Navbar
