import React, { Component } from 'react'
import Axios from 'axios'
import Navbar from './Navbar'


export class Viewmore extends Component {
   constructor(props){
       super()
       this.state = {
           userDetails:[],
           userData:[],
           tokenId: localStorage.getItem("token"),
           flag :false

       }
   }
//    console.log()
componentDidMount = ()=>{
    Axios.get("http://127.0.0.1:5000/get-user-token", {
        headers: {
            Authorization: "Bearer " + this.state.tokenId,
            'Content-Type': 'application/json'
        }
    })
        .then(response => {
            console.log(response.data.user_id)
            this.setState({
                userDetails: response.data
            })
            Axios.get("http://127.0.0.1:5000/users/request_list/"+ response.data.user_id)
            .then(res=>{
                if (res.response == "already send"){
                    alert("already senede")
                }
                else{

                }
                   console.log(res.data)
                   this.setState({
                    userData: res.data
                })
            })
        })

}
onAccept =()=>{
    var connectionDetails = {
        user_id:this.props.match.params.user_id,
        current_userid: this.state.userDetails.user_id
    }
    Axios.post("http://127.0.0.1:5000/accept_connection",connectionDetails)
            .then((response) => {
                console.log(response.data)
                window.location.reload(false);
                this.setState({
                    flag:true
                })
            })
            .catch((err) => alert(err))
}   

    render() {
        let requestdata = this.state.userData.map(a=>{
            return(
                <div className = "container mt-4">
                <div className = "card w-50 offset-3">
                    <img  src={`http://127.0.0.1:5000/${a.postImageLink}`} style={{ width: "50px", height: "50px" }} className=" offset-3 rounded-circle" alt = "image"></img>
                   <p className = "offset-3 "> name:{a.first_name}</p>
                   <p className = "offset-3 "> city:{a.profile.city}</p>
                   <p className = "offset-3 "> marital_status:{a.profile.marital_status}</p>
                   <p className = "offset-3 "> Education:{a.profile.Education}</p>
                   <p className = "offset-3 "> Gender:{a.Gender}</p>
                   <p className = "offset-3 "> DateOfBirth:{a.DateOfBirth}</p>
                   <div>
                   <button className="btn btn-success offset-2" onClick={this.onAccept} >accept request</button>
                   <button className="btn btn-danger offset-2" onClick={this.onDelete} >delete</button>

                   {/* <Link to={`/ViewUsers/${e._id.$oid}`}><button className="btn btn-primary">View Profile</button></Link> */}

                   </div>
                </div>
                </div>
            )
        })
        return (
            <div>
                <Navbar  />
                <h4 className = "text-center"> Freind Request list</h4>
                {requestdata}
            </div>
        )
    }
}

export default Viewmore
