import React from 'react'
import {AppBar,Toolbar,Typography ,Button}from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import classes from './Home.css';
import LoginUser from './LoginUser'
import SignUpUser from './SignpUser';
import axios from 'axios';
import classes1 from './Navbar.css';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import {Link} from 'react-router-dom'


class Navbar extends React.Component {
    constructor(props){
        super(props)
        this.state={
            tokenId: localStorage.getItem("token"),
            user_id:'',
            userDetails:[]

        }
    }
    componentDidMount = (e) => {

      axios.get("http://127.0.0.1:5000/get-user-token", {
          headers: {
              Authorization: "Bearer " + this.state.tokenId,
              'Content-Type': 'application/json'
          }
      })
          .then(response => {
              console.log(response.data)
              this.setState({
                  userDetails: response.data
              })
          })
          .catch((err) => alert(err))
  }
    render(){
      console.log(this.state.userDetails)
        return(

            <div>
        {this.state.tokenId?
        <div className={classes1.root}>
        <AppBar position="static">
          <Toolbar>
            <IconButton
              edge="start"
              className={classes1.menuButton}
              color="inherit"
              aria-label="open drawer"
            >
              <MenuIcon />
            </IconButton>
            <Typography className={classes1.title} variant="h6" noWrap>
              Material-UI
            </Typography>
            <div className={classes1.search}>
              <div className={classes1.searchIcon}>
                <SearchIcon />
              </div>
              <InputBase
                placeholder="Search…"
                classes={{
                  root: classes1.inputRoot,
                  input: classes1.inputInput,
                }}
                inputProps={{ 'aria-label': 'search' }}
              />
            </div>
            <Link class="ml-5 text-dark" to="/CreateTwite"><Button color="inherit">Create twitte</Button></Link>
            <Link class="ml-5 text-dark" to="/ShowAllUsers"><Button color="inherit">ShowAllUsers</Button></Link>
            <Link class="ml-5 text-dark" to="/"><Button color="inherit" onClich = {this.onDelete}>Logout</Button></Link>
            <div className="ml-5 mt-1 h4">{this.state.userDetails.first_name}</div>
            <img src={`http://127.0.0.1:5000/${this.state.userDetails.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>

          </Toolbar>
        </AppBar>
      </div>
    :
     <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
            {/* <MenuIcon /> */}
          </IconButton>
          <Typography variant="h3"  style ={{marginLeft:"200px"}} className={classes.title}>
           facebook
          </Typography>
          <LoginUser props={this.props}/>
        </Toolbar>
      </AppBar>
      <div style ={{marginLeft:"800px"}}>
          <SignUpUser props = {this.props} />
      </div>
    </div>
 }
            </div>
        )


    }
}
export default Navbar
