import React, { Component } from 'react'
import { Link } from "react-router-dom";
import axios from 'axios'
import Navbar from './Navbar';

export default class UserAllDetails extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user_id:"",
            allUser:[],
            AllProfile : [],
            tokenId: localStorage.getItem("token")

        }
    }
    componentDidMount = () => {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data.user_id)
                this.setState({
                    user_id: response.data.user_id,
                    userDetails:response.data
                })

                //Show All Users
                axios.get("http://127.0.0.1:5000/read_user/"+this.state.user_id)

                .then((response) => {
                    console.log(response.data)
                    this.setState({
                        allUser: response.data,

                    })
                })
                .catch((err) => alert(err))
            })   
        }


    render() {
        console.log(this.state.allUser)
        let allUser = this.state.allUser.map(e=>{
            return(
                <div className = "card w-75 offset-1 mt-2">
                    <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{  height: "300px" }}  alt = "image"></img>
                   <Link to={`/EditProfilePic/${this.state.user_id}`}><button className="btn" style = {{marginLeft:"900px" , marginTop:"-30px"}}> Edit Photo</button></Link> 
                    <img src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "150px", height: "150px",marginTop:"-70px" }} className="  rounded-circle mb-2" alt = "image"></img>
                     <div className = "h4 offset-2" style = {{marginTop:"-90px"}}> name : {e.first_name} {e.last_name}</div>
                    <div className = "h4 offset-2"> {e.DateOfBirth}</div>
                    <div className = "h4 offset-2"> {e.Gender}</div>
                    <div className = "h4 offset-2"> {e.profile.city}</div>
                    <div className = "h4 offset-2"> {e.profile.Education}</div>
                    <div className = "h4 offset-2"> {e.profile.marital_status}</div>
                   <Link to={`/Dashboard/${this.state.user_id}`}><button className="btn btn-primary" style = {{marginTop:"-20px"}}>Edit Profile</button></Link> 
                   <Link to={`/Userstory/${this.state.user_id}`}><button className="btn btn-primary offset-4 w-25 mt-2 btn-lg"> all Story</button></Link>

                </div>
            )
        })
        return (
            <div>
            <Navbar/>
            <div>
                {allUser}
            </div>
            </div>
        )
    }
}
