import React, { Component } from 'react';
import Navbar from './Navbar';
import {TextField,Paper,Button  } from '@material-ui/core';


export class Signup extends Component {
  constructor(props) {
    super(props)
    this.state = {
        allCategories: [],
        name: "",
        email: "",
        password: '',
        mobile: "",

    }
}
onChange = (e) => {
    this.setState({
        [e.target.name]: e.target.value
    })
}

onSubmit = (e) => {
    e.preventDefault()
    console.log(this.state)
    let allData = {
        name: this.state.name,
        email: this.state.email,
        password: this.state.password,
        mobile: this.state.mobile
    }

    axios.post("http://127.0.0.1:5000/signup_user", allData)
        .then(response => {
            console.log(response.data)
            this.props.history.push('/Login')
        })
        .catch(error => {
            console.log(error)
        })

}

    render() {
        return (
    <div>

    <h1  className="text-info mt-5"  style = {{textAlign:"center"}}> Login here</h1>

    <form  onSubmit = {this.onSubmit} className =" offset-4 " noValidate autoComplete="off">
    <Paper className =" w-50 mt-5" style={{height:"300px"}}>
        <div>

        <TextField
        className = "ml-5 mb-3"
          id="standard-multiline-flexible"
          label="Enter Name"
          placeholder="Enter name"
          multiline
          rowsMax="4"
          onChange = {this.onChange }
        />
        </div>
        <div>
        <TextField
        className = "ml-5 mb-3"
          id="standard-textarea"
          label="Enter Email"
          placeholder="Enter email"
          multiline
          onChange = {this.onChange }
        />
        </div>
        <div>
        <TextField
        className = "ml-5 mb-3"
          id="standard-textarea"
          label="Number"
          placeholder="Enter Number"
          type="number"
          multiline
          onChange = {this.onChange }
        />
         <TextField
        className = "ml-5 mb-3"
          id="standard-textarea"
          label="Password"
          placeholder="Enter Number"
          type="password"
          multiline
          onChange = {this.onChange }
        />

        </div>
        <Button variant="contained" type = "submit" className = "ml-5" color="secondary">
        Submit
        </Button>
    </Paper>
        </form>
     </div>
        )
    }
}

export default Signup
