import React from 'react';
import axios from 'axios';
import Navbar from './Navbar';



class Photoupload extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            postImageLink: '',
            image: '',
            tokenId : localStorage.getItem("token"),
            user_id:"",
            first_name:""
        }
    }
    componentDidMount = ()=>{
        axios.get("http://127.0.0.1:5000/get-user-token",{
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
        .then(response => {
            console.log(response.data)
            this.setState({
                first_name: response.data. first_name,
                user_id:response.data.user_id
            })
        })
        .catch((err) => alert(err))
        }


    inputChange = (inputData) => {
        // console.log(inputData.target.files[0])
        this.setState({postImageLink: inputData.target.files[0]})
    }

    inputSubmit = (dataSubmit) => {
        dataSubmit.preventDefault();
        const formData = new FormData();
        formData.append('postImageLink',this.state.postImageLink)
        axios.post("http://127.0.0.1:5000/Picture/updateByuser", formData, {
            headers: {
                user_id :this.state.user_id
            }
        })
        .then(response => {
            this.setState({image: response.data.image})
        })
        .catch(error => {
            console.log(error)
        })
    }

    render(){
        // console.log(this.state.userData.picture)
        console.log(this.state.user_id)
        return(
            <React.Fragment>
                <Navbar />
                <div className="container">
                    <div className="row mt-5 ">
                        <div className="col-4">

                        </div>
                        <div className="col-4">
                            <div class="card">
                                <img src={`http://127.0.0.1:5000/${this.state.image}`} class="card-img-top" />
                                <form onSubmit={this.inputSubmit}>
                                    <div class="form-group">
                                        <label>Choose Picture</label>
                                        <input type="file" class="form-control-file" name = "postImageLink" onChange={this.inputChange} />
                                        <button type="submit" className="btn btn-success">Add Photo</button>
                                    </div>  
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}

export default Photoupload;
