import React, { Component } from 'react'
import { Link } from "react-router-dom";
import axios from 'axios'
import Navbar from './Navbar';

export default class ShowAllUsers extends Component {
    constructor(props) {
        super(props)

        this.state = {
            user_id: "",
            allUser: [],
            tokenId: localStorage.getItem("token")

        }
    }
    componentDidMount = () => {
        axios.get("http://127.0.0.1:5000/get-user-token", {
            headers: {
                Authorization: "Bearer " + this.state.tokenId,
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                console.log(response.data.user_id)
                this.setState({
                    user_id: response.data.user_id,
                    userDetails: response.data
                })

                //Show All Users
                axios.get("http://127.0.0.1:5000/ShowAll_user1", {
                    headers: {
                        user_id: response.data.user_id
                    }
                })
                    .then((response) => {
                        console.log(response.data)
                        this.setState({
                            allUser: response.data
                        })
                    })
                    .catch((err) => alert(err))
            })
    }
    render() {
        let allUser = this.state.allUser.map(e => {
            return (
                <div className = "card w-50 mt-5">
                    <img  src={`http://127.0.0.1:5000/${e.postImageLink}`} style={{ width: "50px", height: "50px" }} className=" offset-3 rounded-circle" alt = "image"></img>                    
                    <div className = "h4 offset-4">{e.first_name} {e.last_name} </div>
                    <div>{e.profile.city}</div>
                    <Link to={`/ViewUsers/${e._id.$oid}`}><button className="btn btn-primary">View Profile</button></Link>
                </div>
            )
        })
        return (
            <div>
                <Navbar />
                <div className="offset-4">
                    {allUser}
                </div>
            </div>
        )
    }
}
