import React, { Component } from 'react'
// import { Link } from "react-router-dom";
import axios from 'axios'
import Navbar from './Navbar'

export default class ViewUsers extends Component {
   constructor(props){
       super(props)
       this.state = {
        user_id: "",
        allUser: [],
        tokenId: localStorage.getItem("token")


       }
   }
   componentDidMount = () => {
    axios.get("http://127.0.0.1:5000/get-user-token", {
        headers: {
            Authorization: "Bearer " + this.state.tokenId,
            'Content-Type': 'application/json'
        }
    })
        .then(response => {
            console.log(response.data.user_id)
            this.setState({
                user_id: response.data.user_id,
                userDetails: response.data
            })

            //Show All Users
            axios.get("http://127.0.0.1:5000/read_user", {
                headers: {
                    user_id: this.props.match.params.user_id
                }
            })
                .then((response) => {
                    console.log(response.data)
                    this.setState({
                        allUser: response.data
                    })
                })
                .catch((err) => alert(err))
        })
}

   render(){
       let userDetails = this.state.allUser.map((a)=>{
           return(
               <div>
                  <img src={`http://127.0.0.1:5000/${a.postImageLink}`} style={{ width: "50px", height: "50px" }} className="ml-5 rounded-circle" alt = "image"></img>
                   <p> city:{a.profile.city}</p>
                   <button className="btn btn-danger offset-3 " >following</button>

               </div>
           )
       })
       return(
           <div>
               <Navbar />
               {userDetails}
           </div>
       )
   }

}
